        update(){

        },
        variables:{
            roomId
        }
    })
    const dbt={display:"flex",justifyContent:"center"}
    return(
        <Button style={dbt} as="div" color="red" onClick={DeleteRoom}>
            <Icon name="trash" />
        </Button>
    )
}


const DELETE_ROOM = gql`
